def contar_palabras(mensaje):

    #mensaje.lower() convierte todo el mensaje en minúsculas
    #split() divide el mensaje en palabras, creando una lista de palabras
    palabras = mensaje.lower().split()

    #Se crea un diccionario vacío para hacer un seguimiento de cuántas veces aparece cada palabra en el mensaje
    conteo = {}

    # Recorre las palabras en el mensaje
    # Palabra es una variable, podría tener cualquier otro nombre
    for palabra in palabras:
        if palabra in conteo: #
            # Si la palabra ya está en el diccionario, incrementa su contador
            conteo[palabra] += 1
        else:
            # Si la palabra no está en el diccionario, agrégala con contador 1
            conteo[palabra] = 1

    # Crea una lista de palabras únicas junto con su frecuencia (cantidad de veces que aparece en el mensaje)
    palabras_unicas = [f'{palabra}{conteo[palabra]}' for palabra in conteo]

    # Combina las cadenas de texto en la lista palabras_unicas en una sola cadena de texto (como en el ejemplo de resultado del reto)
    resultado = ''.join(palabras_unicas)

    return resultado

#Mostrar resultado
resultado = contar_palabras(mensaje)
print(resultado)
